// This JavaScript lives in its own file
document.addEventListener('DOMContentLoaded', function() {
    document.querySelector('h1').style.color = 'green';
    console.log('Page loaded from external file!');
});

function loadScript(url, callback) {
    const script = document.createElement('script');
    script.src = url;
    script.onload = callback;
    document.head.appendChild(script);
}

loadScript('https://cdn.example.com/library.js', function() {
    console.log('Script loaded successfully!');
    library.initialize();
});

document.querySelector('#load-feature').addEventListener('click', async () => {
    try {
        const featureModule = await import('./features/cool-feature.js');
        featureModule.initialize();
        console.log('Feature loaded and initialized!');
    } catch (error) {
        console.error('Failed to load feature:', error);
    }
});

// A simple bookmarklet that makes the page editable
javascript:(function(){
    document.body.contentEditable = true;
    document.designMode = 'on';
    alert('Page is now editable! Click anywhere and start typing.');
})();

// Open your browser's console and type:
document.querySelectorAll('img').forEach(img => {
    img.style.transform = 'rotate(180deg)';
});

// Modern bundlers enable splitting code into chunks
import('./features/user-profile.js')
    .then(module => {
        module.initializeProfile();
    });
