sections.code:
// These are equivalent in JavaScript!
let name = "JavaScript"
let age = 28

// The same as:
let name = "JavaScript";
let age = 28;

// This is a single-line comment

/* This is a multi-line comment
   It can span several lines
   Very useful for longer explanations */

// Comments can explain WHY, not just WHAT
let magicNumber = 42;  // The answer to life, the universe, and everything

/**
 * This is a JSDoc comment
 * @param {string} name - The person's name
 * @returns {string} A personalized greeting
 */
function greet(name) {
    return `Hello, ${name}!`;
}

// Valid identifiers
let userName;
let user_name;
let $specialVar;
let _privateVar;
let camelCaseIsCommon;
let Ï€ = 3.14159;  // Yes, you can use Unicode!

// Invalid identifiers
// let 123abc;      // Can't start with a number
// let my-variable; // Can't contain hyphens
// let for;         // Can't use reserved keywords

var oldSchool = "I've been around since the beginning";
let flexible = "I was introduced in ES6/2015";
const rockSolid = "My value cannot be reassigned";

// var is function-scoped
function varDemo() {
    if (true) {
        var x = 10;
    }
    console.log(x);  // 10 - x is visible here!
}

// let and const are block-scoped
function letDemo() {
    if (true) {
        let y = 20;
    }
    // console.log(y);  // Error! y is not defined outside the block
}

// Constants must be initialized and can't be reassigned
const PI = 3.14159;
// PI = 3;  // This would cause an error!

// But objects assigned to constants can be modified
const person = { name: "Alice" };
person.name = "Bob";  // This works!
// person = { name: "Charlie" };  // This would cause an error!

// Number - includes integers and floating point
let age = 25;
let price = 9.99;
let infinity = Infinity;
let notANumber = NaN;  // Still a Number type!

// String - text enclosed in quotes
let single = 'Single quotes work'
let double = "Double quotes work too";
let backtick = `Template literals allow ${age} to be embedded`;

// Boolean - true or false
let isAwesome = true;
let isBoring = false;

// Undefined - value not assigned
let notDefined;
console.log(notDefined);  // undefined

// Null - explicitly assigned "no value"
let empty = null;

// Symbol - unique identifiers (ES6)
let uniqueKey = Symbol("description");
let anotherKey = Symbol("description");  // Different from uniqueKey!

// BigInt - for very large integers (ES2020)
let hugeNumber = 9007199254740991n;  // Note the 'n' suffix

// Object literals - collection of key/value pairs
let person = {
    name: "JavaScript Enthusiast",
    age: 5,
    skills: ["Problem Solving", "Creating Interactivity"],
    greet: function() {
        return `Hi, I'm ${this.name}!`;
    }
};

// Accessing object properties
console.log(person.name);          // Dot notation
console.log(person["age"]);        // Bracket notation
console.log(person.greet());       // Method call

// Arrays - special kind of object
let colors = ["red", "green", "blue"];
console.log(colors[0]);            // "red"
console.log(colors.length);        // 3

// Functions - also objects in JavaScript!
function multiply(a, b) {
    return a * b;
}
console.log(multiply(5, 6));       // 30

// Logical operators
console.log(true && true);   // AND: true
console.log(true && false);  // AND: false
console.log(true || false);  // OR: true
console.log(!true);          // NOT: false

// Short-circuit evaluation
console.log(false && expensiveOperation());  // false, expensiveOperation never runs!
console.log(true || expensiveOperation());   // true, expensiveOperation never runs!

// Logical operators with non-boolean values
console.log("hello" && "world");  // "world" (last truthy value)
console.log("" && "world");       // "" (first falsy value)
console.log("hello" || "world");  // "hello" (first truthy value)
console.log("" || "world");       // "world" (first truthy value or last value)

// Nullish coalescing operator (ES2020)
let username = null;
console.log(username ?? "Guest");  // "Guest" (if left side is null/undefined)

// Basic if
if (age >= 18) {
    console.log("Adult");
}

// If/else
if (temperature > 30) {
    console.log("It's hot!");
} else {
    console.log("It's not too hot.");
}

// If/else if/else
if (score >= 90) {
    console.log("A grade");
} else if (score >= 80) {
    console.log("B grade");
} else if (score >= 70) {
    console.log("C grade");
} else {
    console.log("Needs improvement");
}

// Conditional (ternary) operator
let status = age >= 18 ? "Adult" : "Minor";
